sys_script_client.script:
function onSubmit() {
    // Debug: Log all field values
    console.log('=== FORM VALIDATION DEBUG ===');

    var gender = g_form.getValue('gender');
    console.log('Gender value: ' + gender);

    var branch = g_form.getValue('branch');
    console.log('Branch value: ' + branch);

    var mobileNumber = g_form.getValue('mobile_number');
    console.log('Mobile Number value: ' + mobileNumber);

    // Test basic validation
    if (!gender || gender === '' || gender === 'none') {
        console.log('Gender validation failed');
        g_form.showFieldMsg('gender', 'Gender is required. Please select a valid option.', 'error');
        return false;
    }

    if (!branch || branch === '' || branch === 'none') {
        console.log('Branch validation failed');
        g_form.showFieldMsg('branch', 'Branch is required. Please select a valid option.', 'error');
        return false;
    }

    if (mobileNumber && mobileNumber !== '') {
        console.log('Validating mobile number: ' + mobileNumber);
        var cleanMobileNumber = mobileNumber.replace(/\D/g, '');
        console.log('Clean mobile number: ' + cleanMobileNumber);

        if (cleanMobileNumber.length !== 10) {
            console.log('Mobile number length validation failed: ' + cleanMobileNumber.length);
            g_form.showFieldMsg('mobile_number', 'Mobile number must contain exactly 10 digits.', 'error');
            return false;
        }

        if (!/^\d{10}$/.test(cleanMobileNumber)) {
            console.log('Mobile number format validation failed');
            g_form.showFieldMsg('mobile_number', 'Mobile number must contain only digits.', 'error');
            return false;
        }
    }

    console.log('All validations passed');
    return true;
}
