sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading) {
        return;
    }

    // Validate mobile number on change
    if (newValue && newValue !== '') {
        // Remove any non-digit characters for validation
        var cleanMobileNumber = newValue.replace(/\D/g, '');

        // Check if exactly 10 digits
        if (cleanMobileNumber.length !== 10) {
            g_form.showFieldMsg('mobile_number', 'Mobile number must contain exactly 10 digits.', 'error');
            return;
        }

        // Check if contains only digits
        if (!/^\d{10}$/.test(cleanMobileNumber)) {
            g_form.showFieldMsg('mobile_number', 'Mobile number must contain only digits.', 'error');
            return;
        }

        // Clear error if valid
        g_form.hideFieldMsg('mobile_number');
    } else {
        // Clear error if field is emptied
        g_form.hideFieldMsg('mobile_number');
    }
}
