sys_script_client.script:
function onSubmit() {
    // Check if Gender field is empty or none
    var gender = g_form.getValue('gender');
    if (!gender || gender === '' || gender === 'none') {
        g_form.showFieldMsg('gender', 'Gender is required. Please select a valid option.', 'error');
        return false;
    }

    // Check if Branch field is empty or none
    var branch = g_form.getValue('branch');
    if (!branch || branch === '' || branch === 'none') {
        g_form.showFieldMsg('branch', 'Branch is required. Please select a valid option.', 'error');
        return false;
    }

    // If both validations pass, allow form submission
    return true;
}

// Optional: Add onChange validation for real-time feedback
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading) {
        return;
    }

    // Clear error messages when user makes a selection
    if (control === 'gender' && newValue && newValue !== '' && newValue !== 'none') {
        g_form.hideFieldMsg('gender');
    }

    if (control === 'branch' && newValue && newValue !== '' && newValue !== 'none') {
        g_form.hideFieldMsg('branch');
    }
}
